inputs.transformScript:
return function (inputObject) { 
  if (inputObject && inputObject.listItem) {
    return { url: inputObject.listItem.href };
  }
  return { url: '' }; 
}

inputs.testDataScript:
return function() {
  const outputObject = {
    listItem: {
      href: '/test-page'
    }
  };
  return outputObject;
};
